    private void ponerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ponerActionPerformed

        enteros.push(Integer.parseInt(numero.getText()));
        texto.setText("Numero a√±adido correctamente");
    }//GEN-LAST:event_ponerActionPerformed

    private void quitarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_quitarActionPerformed
        if(enteros.contenidoPila()=="")
            texto.setText("No hay nada");
        else{
        enteros.pop();
        texto.setText("Numero elimindo correctamente");
        }
    }//GEN-LAST:event_quitarActionPerformed

    private void reporteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_reporteActionPerformed
        texto.setText(enteros.contenidoPila());
    }//GEN-LAST:event_reporteActionPerformed

    private void inversoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_inversoActionPerformed
        texto.setText(enteros.contenidoPilaInverso());
    }//GEN-LAST:event_inversoActionPerformed

